import React, { useContext } from "react";
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import { ColorModeContext } from "../../theme";
import { ThemeProvider, useTheme } from "@mui/material";
import numeral from "numeral";
import { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';





const Detail = () => {
  const [country, setCountry] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  const { countryCode } = useParams();

  const borders = country.map((country) => country.borders);

  useEffect(() => {
      const getCountryByCode = async () => {
          try {
              const res = await fetch(`https://disease.sh/v3/covid-19/countries/${country}`,
                  {
                      method: "GET",


                  });

              if (!res.ok) throw new Error("Could not found!");

              const data = await res.json();

              setCountry(data);
              setIsLoading(false);
          } catch (error) {
              setIsLoading(false);
              setError(error.message);
          }
      };

      console.log(countryCode);
      getCountryByCode();
  }, [countryCode]);

  return (
      <div className="country__info__wrapper">


          {isLoading && !error && <h4>Loading........</h4>}
          {error && !isLoading && { error }}

          {country?.map((country, index) => (
              <div  key={index}>
                  <div >
                      <img src={country.flag} alt="" />
                  </div>

                  <div >
                      <h3>{country.name}</h3>

                      <div >

                          <h5>
                              code: <span>{country.code}</span>
                          </h5>
                          {/*<h5>*/}
                          {/*    id: <span>{country.id}</span>*/}
                          {/*</h5>*/}

                      </div>
                  </div>
              </div>
          ))}
      </div>
  );
};

export default Detail;
