import React, { useContext } from "react";
import CssBaseline from '@mui/material/CssBaseline';
import Container from '@mui/material/Container';
import { ColorModeContext } from "../../theme";
import { ThemeProvider, useTheme } from "@mui/material";
import numeral from "numeral";
import { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';





const Detail = () => {
  const [countryInf, setCountryInf] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  const { country } = useParams();

  const borders = country.map((country) => country.borders);

  useEffect(() => {
    const getSingleCountry = async() => {
      try {
          const res = await fetch(`https://disease.sh/v3/covid-19/countries${country}`)
          const data =await res.json()
          setCountryInf(data)

      } catch (error) {
          console.log(error)

      }
  }

      console.log(country);
      getSingleCountry();
  }, [country]);

  return (
      <div className="country__info__wrapper">


          {isLoading && !error && <h4>Loading........</h4>}
          {error && !isLoading && { error }}

          {country?.map((country, index) => (
              <div  key={index}>
                  <div >
                      <img src={country.flag} alt="" />
                  </div>

                  <div >
                      <h3>{country.name}</h3>

                      <div >

                          <h5>
                              code: <span>{country.code}</span>
                          </h5>

                          <div className="info-container">
				<div className="info-flag" style={{backgroundImage: `url(${country.countryInfo.flag})`}} />
				<div className="info-confirmed">Cases: {numeral(country.cases).format("0,0")}</div>
				<div className="info-recovered">Recovered: {numeral(country.recovered).format("0,0")}</div>
				<div className="info-deaths">Deaths: {numeral(country.deaths).format("0,0")}</div>
				</div>


                      </div>
                  </div>
              </div>
          ))}
      </div>
  );
};

export default Detail;
