import React, { useContext } from "react";
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import GitHubIcon from '@mui/icons-material/GitHub';
import FacebookIcon from '@mui/icons-material/Facebook';
import TwitterIcon from '@mui/icons-material/Twitter';
//import { createTheme, ThemeProvider } from '@mui/material/styles';
import Header from './Header';
import MainFeaturedPost from './MainFeaturedPost';
import FeaturedPost from './FeaturedPost';
import Main from './Main';
import Footer from './Footer';
import post1 from './markdown/blog-post.1.md';
import post2 from './markdown/blog-post.2.md';
import post3 from './markdown/blog-post.3.md';

import { ColorModeContext } from "../theme";
import { ThemeProvider, useTheme } from "@mui/material";


const sections = [
  { title: 'Technology', url: '/pages/technology' },

];

const mainFeaturedPost = {
  title: 'COVİD-19 TRACKER PROJECT',
  description:"Coronavirus disease (COVID-19) is an infectious disease caused by the SARS-CoV-2 virus. Most people infected with the virus will experience mild to moderate respiratory illness and recover without requiring special treatment. However, some will become seriously ill and require medical attention. Older people and those with underlying medical conditions like cardiovascular disease, diabetes, chronic respiratory disease, or cancer are more likely to develop serious illness. Anyone can get sick with COVID-19 and become seriously ill or die at any age.",
  image: 'https://source.unsplash.com/random?covid19',
  imageText: 'main image description',
  linkText: 'Continue reading…',
};



const posts = [post1, post2, post3];



// TODO remove, this demo shouldn't need to reset the theme.
//const [theme, colorMode] = useMode();


const Home = () => {
  const theme = useTheme();
  //    const colors = tokens(theme.palette.mode);
  const colorMode = useContext(ColorModeContext);
  return (
    <ColorModeContext.Provider value={colorMode}>
      <ThemeProvider theme={theme}>

        <CssBaseline />
        <Container maxWidth="lg">
          <Header title="Blog" sections={sections} />
          <main>
            <MainFeaturedPost post={mainFeaturedPost} />


          </main>
        </Container>
        <Footer 
          title="Footer"
          description="Something here to give the footer a purpose!"
        />
      </ThemeProvider>
    </ColorModeContext.Provider>
  )

}

export default Home;
